    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveButtonActionPerformed
        DatabaseConnectivity db = new DatabaseConnectivity();

        String isbn = txt_isbn.getText();
        String name = txt_name.getText();
        String edition = txt_edition.getText();
        String publisher = txt_publisher.getText();
        String price = txt_price.getText();
        String page = txt_pages.getText();

        String sql_qry = "UPDATE book SET isbn = '" + isbn + "', name = '" + name + "', edition = " + edition + " , publisher = '" + publisher + "', price = " + price + ", pages = " + page + " WHERE isbn = " + BookList.book_isbn;
        boolean r = db.execute(sql_qry);

        if (r) {
            this.setVisible(false);
            new BookList().setVisible(true);
            Tools.Helper.show(this, "Book is updated");
        } else {
            Tools.Helper.show(this, "opps, please try again!");
        }
    }//GEN-LAST:event_saveButtonActionPerformed

    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        Tools.Helper.setCenter(this);
        DatabaseConnectivity db = new DatabaseConnectivity();
        String isbn = BookList.book_isbn;
        String[][] data = db.Select("SELECT * FROM book WHERE isbn = " + isbn);

        txt_isbn.setText(data[0][0]);
        txt_name.setText(data[0][1]);
        txt_edition.setText(data[0][2]);
        txt_publisher.setText(data[0][3]);
        txt_price.setText(data[0][4]);
        txt_pages.setText(data[0][5]);


    }//GEN-LAST:event_formWindowOpened

