    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        DatabaseConnectivity db = new DatabaseConnectivity();
        db.rs = db.executeQuery("SELECT * FROM user WHERE username = '" + txt_username.getText().toString() + "'");
        try {
            if (db.rs.next()) {
                txt_name.setText(db.rs.getString("name"));
                txt_sec_q.setText(db.rs.getString("sec_q"));
            }
        } catch (SQLException ex) {
            Logger.getLogger(Forgot.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        DatabaseConnectivity db = new DatabaseConnectivity();
        db.rs = db.executeQuery("SELECT * FROM user WHERE username = '" + txt_username.getText() + "' AND answer = '" + txt_answer.getText() + "'");
        try {
            if (db.rs.next()) {
                txt_password.setText(db.rs.getString("password"));
            }else{
                Tools.Helper.show(this, "Incorrect answer");
            }
        } catch (SQLException ex) {
            Logger.getLogger(Forgot.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

