    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        Tools.Helper.setCenter(this);
        DatabaseConnectivity db = new DatabaseConnectivity();

        String[][] data = db.Select("SELECT * FROM member");
        String[] title = {"ID", "Name", "Last Name", "Gender", "Phone No"};

        jTable1 = new JTable(data, title);
        jScrollPane1.setViewportView(jTable1);
    }//GEN-LAST:event_formWindowOpened

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        DatabaseConnectivity db = new DatabaseConnectivity();

        String id = jTable1.getValueAt(jTable1.getSelectedRow(), 0).toString();
        String qry = "DELETE FROM member WHERE id = " + id;
        boolean r = db.execute(qry);
        if (r) {
            formWindowOpened(null);
            Tools.Helper.show(this, "Member deleted successfully");
        } else {
            Tools.Helper.show(this, "Oops, please try again!");
        }


    }//GEN-LAST:event_jButton2ActionPerformed

    private void btn_editActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_editActionPerformed
        member_id = jTable1.getValueAt(jTable1.getSelectedRow(), 0).toString();
        this.setVisible(false);
        new Member_edit().setVisible(true);
    }//GEN-LAST:event_btn_editActionPerformed

