    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        Tools.Helper.setCenter(this);
        this.setAlwaysOnTop(true);
        DatabaseConnectivity db = new DatabaseConnectivity();
        
        String[] columnName = {"ID", "Name", "Last Name", "ISBN", "Book Name", "Edition", "Loan Date"};
        String[][] issued_detail = db.Select("SELECT loan.id, member.name, last_name, book.isbn, book.name, book.edition, loan.loan_date FROM member INNER JOIN loan ON member.id = loan.member_id INNER JOIN book ON book.isbn = loan.isbn WHERE return_date is null");
        table_issued = new JTable(issued_detail, columnName);
        jScrollPane1.setViewportView(table_issued);
        
        String[][] return_details = db.Select("SELECT loan.id, member.name, last_name, book.isbn, book.name, book.edition, loan.loan_date FROM member INNER JOIN loan ON member.id = loan.member_id INNER JOIN book ON book.isbn = loan.isbn WHERE return_date is not null");
        table_return = new JTable(return_details, columnName);
        jScrollPane3.setViewportView(table_return);
        
    }//GEN-LAST:event_formWindowOpened

