    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        DatabaseConnectivity db = new DatabaseConnectivity();

        String id = usersList.user_id;
        String name = txt_name.getText();
        String username = txt_username1.getText();
        String password = txt_password.getText();
        String sec_q = txt_sec_q.getSelectedItem().toString();
        String answer = txt_answer.getText();

        String qry = "UPDATE user SET username =  '" + username + "', password = '" + password + "', name = '" + name + "', sec_q = '" + sec_q + "', answer = '" + answer + "' WHERE id = '" + id + "'";
        boolean result = db.execute(qry);
        if (result) {
            Tools.Helper.show(this, "Successfully updated");
            txt_name.setText("");
            txt_username1.setText("");
            txt_password.setText("");
            txt_answer.setText("");
        } else {
            Tools.Helper.show(this, "Oops, please try again");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        Helper.setCenter(this);
        DatabaseConnectivity db = new DatabaseConnectivity();
        String[][] userData = db.Select("SELECT * FROM user WHERE id = " + usersList.user_id);
        txt_name.setText(userData[0][3]);
        txt_username1.setText(userData[0][1]);
        txt_password.setText(userData[0][2]);
        txt_sec_q.setSelectedItem(userData[0][4]);
        txt_answer.setText(userData[0][5]);


    }//GEN-LAST:event_formWindowOpened

